import { Component, OnInit, Input } from '@angular/core';
import { FetchApiDataService } from '../fetch-api-data.service';
import { MatSnackBar } from '@angular/material/snack-bar';
import { MatDialog } from '@angular/material/dialog';
import { Router } from '@angular/router';
import { AuthService } from '../auth.service';
import { DeleteAccountComponent } from '../delete-account/delete-account.component';

@Component({
  selector: 'app-account-page',
  templateUrl: './account-page.component.html',
  styleUrls: ['./account-page.component.scss']
})

export class AccountPageComponent implements OnInit {

  username: any;
  user: any = null;
  songs: any[] = [];
  favs: any[] = [];

  constructor(
    public fetchApiData: FetchApiDataService, 
    public snackbar: MatSnackBar,
    public dialog: MatDialog,
    private router: Router,
    private authService: AuthService
  ) {}

  ngOnInit(): void {
    this.username = localStorage.getItem('username');
    this.showUser();
    this.getOnlyFavs();
  }

  getOnlyFavs(): void {
    this.fetchApiData.getAllSongs().subscribe((res: any) => {
      this.songs = res.filter(song => this.favs.includes(song._id)); 
      console.log(this.songs);
      return this.songs; 
    });
  }

  showUser(): void {
    this.fetchApiData.getUser(this.username).subscribe(res => {
      this.user = res; 
      this.favs = res.Favorites || []; 
      console.log(this.user);
    }, (error) => {
      console.error('Failed to fetch user data', error);
    });
  }

  isFavorite(songId: string): boolean {
    return this.favs.includes(songId) ? true : false ; 
  }

  removeFromFavorites(songId: string): void {
    this.favs = this.favs.filter(song => song !== songId);
    this.songs = this.songs.filter(song => this.favs.includes(song._id)); 
    console.log("removeFrom Favorites is doing it's job");
  }

  handleUserUpdate(): void {
    const username = localStorage.getItem('username');
    if(username) {
      this.showUser();
    }
  }

  openDeleteDialog(): void {
    this.dialog.open(DeleteAccountComponent, {width: '300px'});
  }

}

<div class="main">
    <div class="user-info">

        <app-user-info [user]="user">
        </app-user-info>

        <mat-card class="fav-container">

            <mat-card-header>
                <h2>Favorites</h2>
            </mat-card-header>

            <mat-card-content>
                <div *ngIf="favs.length > 0; else noFavorites" class="favorites-array">
                        <app-song-card
                            *ngFor="let song of songs"
                            [song]=song
                            [isFavorite]=isFavorite
                            [user]=user
                            [favs]=favs
                            (songUnfavorited)="removeFromFavorites($event)"
                        >

                        </app-song-card>
                </div>

                <ng-template #noFavorites>
                    <p>You have no favorite songs yet!</p>
                </ng-template>
            </mat-card-content>

        </mat-card>

    </div>
    <div class="update-user">
        <app-user-update-form [user]="user" (userUpdated)="handleUserUpdate()"></app-user-update-form>
    </div>

    <mat-card class="delete-user">
        <mat-card-header><h2>Delete your account</h2></mat-card-header>
        <mat-card-actions class="make-them-stay">
            <button
            mat-raised-button
            color="warn"
            (click)="openDeleteDialog()"
            >
                Delete account
            </button>
        </mat-card-actions>
    </mat-card>
</div>
